package drugdealing.utility;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;
import java.util.Random;

public class RandomSkinSelector {
	private final static int SkinsCount = 5;
	
	private static List<StringPair> SkinsList = new ArrayList<StringPair>(Arrays.asList (
			new StringPair (
					"",
					""
					),
			new StringPair (
					"",
					""
				),
			new StringPair (
					"",
					""
				),
			new StringPair (
					"",
					""
				),
			new StringPair (
					"",
					""
				)
		));
	
	public static StringPair getRandomSkin() {
    	Random random = new Random();
    	int randomNumber = random.nextInt(SkinsCount);
    	
		return SkinsList.get(randomNumber);
	}

}
